<?phpclass ClickCountAction extends BaseAction {    private $token;    public function __construct()    {        $this->token = $_GET['token'];    }    public function menuClick()    {        $model = M('user_menu_click');        $mid = intval( $_POST['mid'] );        $openid = trim($_POST['openid']);        $where['mid'] = $mid;        $where['openid'] = $openid;        $where['token'] = $this->token;        $find = $model->where( $where )->order('click_time desc')->find();        if( $find ){            $cwhere['id'] = $find['id'];            $cdata['is_reached'] = 1;            $res = $model->where( $cwhere )->save( $cdata );        }    }}